mapper.select:
SELECT o.id AS orderId, o.orderNumber, o.`status`, o.price, o.freight FROM t_order o WHERE o.userId = #{userId} AND o.userIsValid = 1
AND o.`status` = #{status}
ORDER BY o.create_time DESC
LIMIT #{limit},#{limitSize}

SELECT
o.*,u.nickName,a.username,addr.detailAddress AS address
FROM t_users u,t_address addr,t_order o
LEFT JOIN admin a ON o.adminId = a.id
WHERE 1 = 1
AND u.id = o.userId
AND addr.id = o.addressId

    AND o.orderNumber like '%${orderNumber}%'


    AND o.expressNumber like '%${expressNumber}%'


    AND u.nickName like '%${nickName}%'


    AND a.username like '%${username}%'


    AND o.status = #{status}


    AND o.payMethod = #{payMethod}


    AND o.userIsValid = #{userIsValid}


    AND o.adminOk = #{adminOk}


    AND o.create_time  >=  #{createTimeStart}


    AND o.create_time  <=  #{createTimeEnd}

ORDER BY
o.create_time DESC

    limit #{pages.pages}, #{pages.size}

SELECT
COUNT(0)
FROM t_users u,t_order o
LEFT JOIN admin a ON o.adminId = a.id
WHERE 1 = 1
AND u.id = o.userId

    AND o.orderNumber like '%${orderNumber}%'


    AND o.expressNumber like '%${expressNumber}%'


    AND u.nickName like '%${nickName}%'


    AND a.username like '%${username}%'


    AND o.status = #{status}


    AND o.payMethod = #{payMethod}


    AND o.userIsValid = #{userIsValid}


    AND o.adminOk = #{adminOk}


    AND o.create_time  >=  #{createTimeStart}


    AND o.create_time  <=  #{createTimeEnd}
