mapper.select:
SELECT p.id AS productId, p.`name` AS productName, p.coverImg AS productCoverImg, p.price AS price, p.promotionPrice AS promotionPrice, (SELECT COUNT(0) FROM t_comment tc WHERE p.id = tc.productId) AS comments, (SELECT (SUM(tc.score) / COUNT(0)) FROM t_comment tc WHERE p.id = tc.productId) AS commentRatio FROM t_product p WHERE 1 = 1
AND p.productCategoryId = #{productCategoryId}
AND p.isPromotion = #{isPromotion}
AND p.isIntegral = #{isIntegral}
AND p.isHot = #{isHot}
ORDER BY commentRatio DESC
LIMIT #{limit},#{limitSize}
